let buttonsBuy=document.querySelectorAll(".products__button_buy"),modalCart=document.querySelector(".popup-cart"),closeCart=document.querySelector(".popup-cart__button_close");for(let t of buttonsBuy)t.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.add("popup-cart_visible")});closeCart.addEventListener("click",function(t){t.preventDefault(),modalCart.classList.remove("popup-cart_visible")}),window.addEventListener("keydown",function(t){27===t.keyCode&&modalCart.classList.contains("popup-cart_visible")&&(t.preventDefault(),modalCart.classList.remove("popup-cart_visible"))});
